package com.example.demo.wildcardmatching;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertTrue;

class WildCardMatchingTest {


    WildCardMatching wildCardMatching = new WildCardMatching();


    @Test
    void shouldReturnCorrectMatch() {

        assertFalse(wildCardMatching.isMatch("aa", "a"));
    }


    @Test
    void shouldReturnCorrectMatch2() {

        assertTrue(wildCardMatching.isMatch("aa", "*"));
    }

    @Test
    void shouldReturnCorrectMatch3() {

        assertFalse(wildCardMatching.isMatch("cb", "?a"));
    }


    @Test
    void shouldReturnCorrectMatch4() {

        assertTrue(wildCardMatching.isMatch("adceb", "*a*b"));
    }

    @Test
    void shouldReturnCorrectMatch5() {

        assertFalse(wildCardMatching.isMatch("babaaababaabababbbbbbaabaabbabababbaababbaaabbbaaab", "***bba**a*bbba**aab**b"));
    }

    @Test
    void shouldReturnCorrectMatch6() {

        assertFalse(wildCardMatching.isMatch(
                "",
                "**"));
    }


}